INSERT INTO CMPNY_INFO ( TAX_NO , CMPNY_NM , CMPNY_CEO_NM , CMPNY_ZIP , CMPNY_ADDR1 , CMPNY_ADDR2 , MANGER_EMAIL , DEL_YN , REG_DT , REG_ID ) VALUES ( #{compRegNo} , #{compName} , CMPNY_CEO_NM = IFNULL(#{compCeo},'') , IFNULL(#{compZip},'') , IFNULL(#{compAddr1},'') , IFNULL(#{compAddr2},'') , IFNULL(#{mangerEmail},'')
, #{delYn}
, 'N'
, DATE_FORMAT(NOW(), '%Y%m%d') , #{regId} ) ON DUPLICATE KEY UPDATE EDT_DT = DATE_FORMAT(NOW(), '%Y%m%d') , EDT_ID = #{edtId} , CMPNY_NM = #{compName} , CMPNY_CEO_NM = IFNULL(#{compCeo},'') , CMPNY_ZIP = IFNULL(#{compZip},'') , CMPNY_ADDR1 = IFNULL(#{compAddr1},'') , CMPNY_ADDR2 = IFNULL(#{compAddr2},'') , MANGER_EMAIL = IFNULL(#{mangerEmail},'')
, DEL_YN = #{delYn}
, DEL_YN = 'N'

		INSERT 
			INTO CMPNY_BRANCH_INFO (
				  TAX_NO
				, BRANCH_CD
				, CUS_PRG_CD
				, DEL_YN 
				, REG_DT
				, REG_ID
			  	 ) VALUES (
       					  #{compRegNo}
       					, #{branchCd}
						, IFNULL(#{cusPrgCd},'')
						 
							
							   , #{delYn}
							
							
							   , 'N'
							
						
						, DATE_FORMAT(NOW(), '%Y%m%d')
						, #{regId}
			) ON DUPLICATE KEY
            UPDATE EDT_DT = DATE_FORMAT(NOW(), '%Y%m%d')
				 , EDT_ID = #{edtId}
				 , TAX_NO = #{compRegNo}
				 , BRANCH_CD = IFNULL(#{branchCd},'')
