 INSERT
	INTO MSG_INFO (
							  MSG_ID
							, MSG_NM_EN
							, MSG_NM_KR
							, DEL_YN
							, REG_DT
							, REG_ID
			) VALUES (
							  #{msgId}
							, #{msgNmEn}
							, #{msgNmKr}
							, 'N'
							, DATE_FORMAT(NOW(), '%Y%m%d')
							, #{regId}
			) ON DUPLICATE KEY
