							  MSG_NM_EN = #{msgNmEn}
							, MSG_NM_KR = #{msgNmKr}
							
								, DEL_YN = #{delYn}
							
							
								, DEL_YN = 'N'
							  
							, EDT_DT = DATE_FORMAT(NOW(), '%Y%m%d')
							, EDT_ID = #{edtId}
