mapper.select:
	SELECT COUNT(*)
    FROM CMPNY_INFO 
    WHERE TAX_NO = #{compRegNo}
	  AND CMPNY_NM = #{compName}
	  AND DEL_YN = 'N'

mapper.insert:
		INSERT
		  INTO USER_INFO (USR_ID
		  				, USR_PW
						, USR_NM
						, USR_EMAIL
						, USR_TELNO
						, ALARM_EMAIL
						, ALARM_SMS
						, ALARM_KAKAO
						, RECV_YN
						, GRP_CD
						, SHINHAN_MANER
						, APPR_YN
						, DEL_YN
						, REG_DT
						, REG_ID
       ) VALUES (		
       					  #{memberId}
       					, #{memberPwd}
						, #{memberName}
						, #{memberEmail}
						, #{memberTel}
						, #{alarmEmail}
						, #{alarmSMS}
						, #{alarmKakao}
						, #{recvYn}
						, #{grpCd}
						, #{shinhanManer}
						, #{apprYn}
						, #{delYn}
						, DATE_FORMAT(NOW(), '%Y%m%d')
						, #{regId}
       ) ON DUPLICATE KEY
            UPDATE EDT_DT = DATE_FORMAT(NOW(), '%Y%m%d')
				 , EDT_ID = #{regId}
				 , USR_NM = #{memberName}
				 , USR_EMAIL = #{memberEmail}
				 , USR_TELNO = #{memberTel}
				 , ALARM_EMAIL = #{alarmEmail}
				 , ALARM_SMS = #{alarmSMS}
				 , ALARM_KAKAO = #{alarmKakao}
				 , SHINHAN_MANER = #{shinhanManer}
				 , GRP_CD = #{grpCd}
				 , RECV_YN = #{recvYn}
				 , APPR_YN = #{apprYn}
 				
					, DEL_YN = 'N'

		INSERT 
			INTO USER_CMPNY_INFO (
			 			  USR_ID 
						, TAX_NO
						, CMPNY_NM
						, MANER_YN
						, DEL_YN
						, REG_DT
						, REG_ID
       ) VALUES (
       					  #{memberId}
						, #{compRegNo}
						, #{compName}
						, #{mangerYn}
						, #{delYn}
						, DATE_FORMAT(NOW(), '%Y%m%d')
						, #{regId}
			) ON DUPLICATE KEY
            UPDATE EDT_DT = DATE_FORMAT(NOW(), '%Y%m%d')
				 , EDT_ID = #{edtId}
				 , TAX_NO = #{compRegNo}
				 , CMPNY_NM = #{compName}
				 , MANER_YN = #{mangerYn}
 				
					, DEL_YN = #{delYn}

		INSERT 
			INTO CMPNY_INFO (
						  TAX_NO
						, CMPNY_NM
						, DEL_YN
						, REG_DT
						, REG_ID
       ) VALUES (
						  #{compRegNo}
						, #{compName}
						, #{delYn}
						, DATE_FORMAT(NOW(), '%Y%m%d')
						, #{regId}
			)
