mapper.select:
SELECT COUNT(*) FROM MENU_INFO a LEFT OUTER JOIN MENU_INFO b ON a.UPPR_MENU_ID = b.MENU_ID AND b.DEL_YN = 'N' WHERE a.DEL_YN = 'N'
AND a.MENU_NM_EN LIKE CONCAT('%',UPPER(#{srch2}),'%')
AND a.MENU_NM_KR LIKE CONCAT('%',UPPER(#{srch1}),'%')
AND a.MENU_TYPE = #{srch4}
AND a.UPPR_MENU_ID = #{srch6}

mapper.insert:
		INSERT
		   INTO MENU_INFO (
						  MENU_ID
						, MENU_TYPE
						, UPPR_MENU_ID
						, URL
						, MENU_NM_EN
						, MENU_NM_KR
						, ORDR_SN
						, DISP_YN
						, DEL_YN
						, REG_DT
						, REG_ID
        		 ) VALUES (
						  #{menuId}
						, #{menuType}
						, #{upprMenuId}
						, #{url}
						, #{menuNmEn}
						, #{menuNmKr}
						, #{ordrSn}
						, #{dispYn}
						, 'N'
						, DATE_FORMAT(NOW(), '%Y%m%d')
						, #{regId}
        ) ON DUPLICATE KEY
             UPDATE UPPR_MENU_ID = #{upprMenuId}
						, URL = #{url}
						, MENU_NM_EN = #{menuNmEn}
						, MENU_NM_KR = #{menuNmKr}
						, ORDR_SN = #{ordrSn}
						, DISP_YN = #{dispYn}
						, EDT_DT = DATE_FORMAT(NOW(), '%Y%m%d')
						, EDT_ID = #{edtId}
